#소수 여부 판단하는 코드
def decimal(n):
    for i in range(2,n+1):
        if n % i ==0:
            return False #소수 아니면 False출력
    #들여쓰기 위치(else로 해도 되고 for문과 같은 라인에 맞춰도 됨)
    return True #소수이면 True 출력

#입력한 수까지 소수인 숫자 판별하기
def prime_number(inputNumber):
    result = []
    for number in range(2, (inputNumber + 1)): #소수에는 1이 포함되지 않음
        flag = True
        for n in range(2, number):
            if number % n == 0:
                flag = False
                break

        if (flag):
            result.append(number)

    return result
